import React, { Component } from 'react';

class RestaurantCreate extends Component {
    constructor() {
        super();
        this.state=
        {
            name:null,
            email:null,
            rating:null,
            address:null

        }
        
    }
    create()
    {

        
    }
    render() {
        return (
            <div>
                <h1>Restaurant Create</h1><br/> <br/> 
                <div> 
                <input onChange={(event)=>{this.setState({name:event.target.name})}}
                placeholder="Resturant Name" /> <br/> <br/> 
                  <input onChange={(event)=>{this.setState({email:event.target.name})}}
                placeholder="Resturant Email" /> <br/> <br/> 
                  <input onChange={(event)=>{this.setState({rating:event.target.name})}}
                placeholder="Resturant Rating" /> <br/> <br/> 
                 <input onChange={(event)=>{this.setState({address:event.target.name})}}
                placeholder="Resturant Address" /> <br/> <br/> 
                <button onClick={()=>{this.create()}}>Add Resturant</button>
                </div>
            </div>
        );
    }
}

export default RestaurantCreate;
